import {
    createStore,
} from 'redux';

export const addToTab = (payload) => ({
    type: 'ADD_TAB',   
    payload                     // <-- action.type
});

export const updateTabDetails = (payload) => ({
    type: 'UPDATE_TAB_DETAILS',   
    payload                     // <-- action.type
});

export const tabReducer=(state =initialState, action)=>{
    console.log("reducer state",state,action.type);
    switch (action.type) {

      case 'UPDATE_TAB_DETAILS':
            return Object.assign({},
                                 state,
                                 { tabItems:[...state.tabItems,action.payload],nextID:state.nextID}
                                )
      case 'ADD_TAB':

            return Object.assign({},
                                 state,
                                 { tabItems:[...state.tabItems,action.payload],nextID:state.nextID+1}
                                )
       default:
            return state;
    }
}



const initialState = { 
    tabItems: [{id:1,name:"Charlie",tablist:["Highly reliable","Good work ethics"]},
               {id:2,name:"Henry",tablist:["Needs impovement","Interested in Sports"]},
               {id:3,name:"Bob",tablist:["Good programmer","high energy"]},
               {id:4,name:"Mary",tablist:["HR department","Admin"]},
               {id:5,name:"Terry",tablist:["Finance","Investor"]},
               {id:6,name:"Jerry",tablist:["Security","parttime"]},
               {id:7,name:"Molly",tablist:["Database","DB2"]},
               {id:8,name:"Luke",tablist:["Infrastrusture","Unix"]}
                   ],
     nextID:8              
};

export function configureStore(initialState = initialState) { // initialState = initialState | {}
    const store = createStore(tabReducer,initialState);
    return store;
};

export const store = configureStore();
