import React, { Component } from "react";
import { addToTab, updateTabDetails} from '../redux.js';
import { connect } from 'react-redux';
import { Link } from "react-router-dom";
import './../style/dojodossier.css'

class DojoDossierMain extends Component {
    constructor(props) {
      super(props);
      this.state={tabcontentclassname:"tabcontentactive"}
      };
    changeClassName=(id)=>{
       for(let i=0;i<this.props.tabItems.length;i++){
         if(id===this.props.tabItems[i].id){

         }
       }
       tabcontent = document.getElementsByClassName("tabcontentdisable");
        for (let i = 0; i < tabcontent.length; i++) {
            tabcontent[i].className = tabcontent[i].className.replace(" tabcontentdisable", "tabcontentactive");

        }

    } 
    render() {
      const displayTab= this.props.tabItems.map((items,index)=>{

         return (
            <div>
                <div className="tab">
                    <button className="tablinks" onclick={()=>{this.changeClassName(items.id)}}>{items.name}</button>
                </div>
                <div id={items.id} className="tabcontentdisable">
                    <p>{items.City}</p> 
                    <p>{items.Hobbies}</p> 
                </div>
            </div> 
         )

      })

      return (
          <div className="outerdiv">
           {displayTab}


          </div>
      );
    }
  }

  const mapStateToProps = (state) => ({
    tabItems:state.tabItems
  })

  const mapDispatchToProps = (dispatch) => ({
    addToTab: (payload) => dispatch(addToTab(payload)),
    updateTabDetails: (payload) => dispatch(updateTabDetails(payload))
  })

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(DojoDossierMain);
